import React, { useState, useEffect } from 'react';
import { patientService } from '../services/patientService';
import { doctorService } from '../services/doctorService';
import { appointmentService } from '../services/appointmentService';
import Loading from '../components/common/Loading';
import CalendarView from '../components/appointments/CalendarView';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalPatients: 0,
    totalDoctors: 0,
    todayAppointments: 0,
    totalRevenue: 0
  });
  const [recentAppointments, setRecentAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Fetch all data in parallel
      const [patientsRes, doctorsRes, appointmentsRes, todayAppointmentsRes] = await Promise.all([
        patientService.getAllPatients(),
        doctorService.getAllDoctors(),
        appointmentService.getAllAppointments({ limit: 5 }),
        appointmentService.getTodaysAppointments()
      ]);

      const patients = patientsRes.data.patients || patientsRes.data;
      const doctors = doctorsRes.data.doctors || doctorsRes.data;
      const appointments = appointmentsRes.data.appointments || appointmentsRes.data;
      const todayAppointments = todayAppointmentsRes.data;

      // Calculate revenue (sum of completed appointment fees)
      const revenue = appointments
        .filter(apt => apt.status === 'Completed')
        .reduce((sum, apt) => sum + (apt.consultationFee || 0), 0);

      setStats({
        totalPatients: patients.length,
        totalDoctors: doctors.length,
        todayAppointments: todayAppointments.length,
        totalRevenue: revenue
      });

      setRecentAppointments(appointments.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <Loading message="Loading Dashboard..." />;
  }

  return (
    <div className="dashboard">
      <div className="page-header">
        <h1>Dashboard</h1>
        <div>Welcome to Hospital Management System</div>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card patients">
          <h3>Total Patients</h3>
          <div className="number">{stats.totalPatients}</div>
        </div>
        <div className="stat-card doctors">
          <h3>Total Doctors</h3>
          <div className="number">{stats.totalDoctors}</div>
        </div>
        <div className="stat-card appointments">
          <h3>Today's Appointments</h3>
          <div className="number">{stats.todayAppointments}</div>
        </div>
        <div className="stat-card revenue">
          <h3>Total Revenue</h3>
          <div className="number">â‚¹{stats.totalRevenue}</div>
        </div>
      </div>

      <div className="dashboard-sections">
        {/* Recent Appointments */}
        <div className="card">
          <h3>Recent Appointments</h3>
          {recentAppointments.length === 0 ? (
            <p style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
              No recent appointments
            </p>
          ) : (
            <div className="appointment-list">
              {recentAppointments.map(appointment => (
                <div key={appointment._id} className="appointment-item">
                  <div className="appointment-info">
                    <strong>{appointment.patientId?.name}</strong>
                    <span>with Dr. {appointment.doctorId?.name}</span>
                  </div>
                  <div className="appointment-meta">
                    <span>{new Date(appointment.appointmentDate).toLocaleDateString()}</span>
                    <span className={`status ${appointment.status.toLowerCase()}`}>
                      {appointment.status}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="card">
          <h3>Quick Actions</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <button 
              className="btn btn-primary"
              onClick={() => window.location.href = '/appointments'}
            >
              Book Appointment
            </button>
            <button 
              className="btn btn-secondary"
              onClick={() => window.location.href = '/patients'}
            >
              Add New Patient
            </button>
            <button 
              className="btn btn-success"
              onClick={() => window.location.href = '/doctors'}
            >
              Add New Doctor
            </button>
          </div>
        </div>
      </div>

      {/* Calendar View */}
      <div style={{ marginTop: '30px' }}>
        <CalendarView appointments={recentAppointments} />
      </div>

      <style jsx>{`
        .appointment-item {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 12px;
          border-bottom: 1px solid #eee;
        }
        .appointment-item:last-child {
          border-bottom: none;
        }
        .appointment-info {
          display: flex;
          flex-direction: column;
        }
        .appointment-info span {
          font-size: 12px;
          color: #666;
        }
        .appointment-meta {
          display: flex;
          flex-direction: column;
          align-items: flex-end;
          gap: 4px;
        }
        .status {
          padding: 2px 8px;
          border-radius: 12px;
          font-size: 11px;
          font-weight: bold;
        }
        .status.scheduled { background: #fff3cd; color: #856404; }
        .status.confirmed { background: #d1ecf1; color: #0c5460; }
        .status.completed { background: #d4edda; color: #155724; }
        .status.cancelled { background: #f8d7da; color: #721c24; }
      `}</style>
    </div>
  );
};

export default Dashboard;
